package com.chrispconnolly.weatherapp

import android.os.Bundle
import android.widget.ImageView
import android.widget.TextView
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.Observer
import androidx.lifecycle.ViewModelProviders
import com.google.gson.Gson
import org.jetbrains.anko.imageResource


class DetailActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_detail)

        val weatherViewModel = ViewModelProviders.of(this).get(WeatherViewModel::class.java)
        weatherViewModel.getWeatherModel(
            intent.getStringExtra("location"),
            intent.getStringExtra("latLong"))
            .observe(this, Observer<WeatherModel> { weatherModel ->
            loadData(weatherModel)
        })
//        loadDataOffline()
    }

    private fun loadData(weatherModel:WeatherModel){
        val currently = weatherModel.currently
        val daily = weatherModel.daily.data[0]
        findViewById<ImageView>(R.id.weatherIcon).imageResource = getImageSrc(currently.icon)
        loadTextView(R.id.location, intent.getStringExtra("location"))
        loadTextView(R.id.temperature, formatTemperature(currently.temperature))
        loadTextView(R.id.summary, currently.summary)
        loadTextView(R.id.highLow, formatHighLow(daily.temperatureHigh, daily.temperatureLow))
        loadTextView(R.id.precipProbability, formatPrecipProbability(currently.precipProbability))
        loadTextView(R.id.windSpeedValue, String.format("${weatherModel.currently.windSpeed.toInt()} MPH"))
        loadTextView(R.id.humidityValue, String.format("${(weatherModel.currently.humidity*100).toInt()}%%"))
        loadTextView(R.id.uvindexValue, String.format(getUvIndexString(weatherModel.currently.uvIndex)))
        loadTextView(R.id.alert, getAlertsString(weatherModel))
    }

    private fun loadTextView(id:Int, text:String){
        findViewById<TextView>(id).text = text
    }

    private fun getUvIndexString(uvindex:Int):String{
        var result = when(uvindex) {
            in 0..2 -> "Low"
            in 3..5 -> "Moderate"
            in 6..7 -> "High"
            in 8..10 -> "Very High"
            in 11..20 -> "Extreme"
            else -> "Low"
        }
        return String.format("$result $uvindex")
    }

    private fun getAlertsString(weatherModel:WeatherModel):String{
        var result = ""
        if(weatherModel.alerts != null) {
            for (alert: WeatherModel.Alert in weatherModel.alerts) {
                result += String.format("${alert.title} - Exp: ${formatDate(alert.expires)}\n")
            }
        }
        return result
    }

